YUI.add("pogo-formatters",function(a){a.namespace("Pogo").Formatters={idFormatter:function(b){b.cell.setContent(a.Lang.sub('<a href="{root}job/{id}">{id}</a>',{root:a.Pogo.Env.root,id:b.value}));return false;},commandFormatter:function(c){var b=a.Pogo.Formatters.truncateText(c.value);c.cell.setContent(a.Lang.sub('<span title="{command}">{command_trunc}</span>',{command:a.Escape.html(c.value),command_trunc:b}));return false;},timeFormatter:function(c){var b=a.DataType.Date.format(new Date(c.value*1000),{format:"%x %X"});return b;},userFormatter:function(b){b.cell.setContent(a.Lang.sub('<a href="{root}user/{username}">{username}</a>',{root:a.Pogo.Env.root,username:b.value}));return false;},targetFormatter:function(c){var b=c.value.join(", ");c.cell.setContent(a.Lang.sub('<span title="{targets}">{targets_trunc}</span>',{targets:a.Escape.html(b),targets_trunc:a.Pogo.Formatters.truncateText(b)}));return false;},hostFormatter:function(b){b.cell.setContent(a.Lang.sub('<a href="{root}job/{jobid}/{hostname}">{hostname}</a>',{root:a.Pogo.Env.root,jobid:b.data.jobid,hostname:b.value}));return false;},truncateText:function(c,b){b=b||20;return c.length>b-3?a.Escape.html(c.substring(0,b)+"..."):a.Escape.html(c);}};},"@VERSION@",{requires:["escape","pogo-env","datatype-date"]});